import React, {useEffect, useState} from 'react';
import './App.css';
import Moment from 'moment';
function App() {
const [lat, setlatitude] = useState(null);
const [long, setlongitude] = useState(null);
const [userdata,
  setUserdata] = useState([]);
const [error,
  setError] = useState(null)
const timessstamp = Moment().unix();
// console.log(timessstamp); return;
  useEffect(() => {
  fetch('http://api.openweathermap.org/geo/1.0/direct?q=London&limit=5&appid=2a6ed6669671100313d8ccd388e8784e').then(async response => {
            const isJson = response
                .headers
                .get('content-type')
                ?.includes('application/json');
            const data = isJson && await response.json();

            // check for error response
            if (!response.ok) {
                // get error message from body or default to response status
                const error = (data && data.message) || response.status;
                return Promise.reject(error);
            }
            // console.log(timessstamp);
            setlatitude(data[0].lat);
            setlongitude(data[0].lon);
        }).catch(error => {
            setError(error.toString());
            console.error('There was an error!', error);
        });
}, [])
      if(lat !== null && long !==null) {
        fetch('http://api.openweathermap.org/data/2.5/onecall/timemachine?lat='+lat+'&lon='+long+'&dt='+timessstamp+'&appid=2a6ed6669671100313d8ccd388e8784e').then(async response => {
          const isJson = response
              .headers
              .get('content-type')
              ?.includes('application/json');
          const data = isJson && await response.json();

          // check for error response
          if (!response.ok) {
              // get error message from body or default to response status
              const error = (data && data.message) || response.status;
              return Promise.reject(error);
          }
          console.log(data.hourly);
          setUserdata(data.hourly);
      }).catch(error => {
          setError(error.toString());
          console.error('There was an error!', error);
      });
    }



  return (
    <div className="App">
      {userdata && userdata.map((note:any) => {
      return (
            {note}
      )})}
    </div>
  );
}

export default App;
